INSERT INTO `bf_sql` VALUES ('2','','create database {db}','a:1:{s:2:\"db\";s:12:\"\";}'), ('4','','create table `{pre}{name}`(\r\n`id` int,\r\n{f}\r\n) ENGINE=MyISAM DEFAULT CHARSET=utf8;','a:2:{s:4:\"name\";s:6:\"\";s:1:\"f\";s:6:\"\";}'), ('6','ID','delete from {pre}members where id=\'{id}\';\r\ndelete from {pre}memberfields where uid=\'{id}\';','a:1:{s:2:\"id\";s:8:\"ID\";}'), ('7','auto_increment','alter table {pre}{tbName} auto_increment={num}','a:2:{s:6:\"tbName\";s:6:\"\";s:3:\"num\";s:9:\"\";}'), ('9','','UPDATE \r\n	{pre}memberfields t0 \r\nLEFT JOIN \r\n	(SELECT \r\n		t0.uid,IFNULL(t1.total,0) sellers1,IFNULL(t2.total,0) sellers2,IFNULL(t3.total,0) sellers3\r\n	FROM\r\n		{pre}memberfields t0\r\n	LEFT JOIN\r\n		(SELECT uid,COUNT(*) total FROM {pre}sellers WHERE type=\'1\' GROUP BY uid) t1\r\n	ON\r\n		t1.uid=t0.uid\r\n	LEFT JOIN\r\n		(SELECT uid,COUNT(*) total FROM {pre}sellers WHERE type=\'2\' GROUP BY uid) t2\r\n	ON\r\n		t2.uid=t0.uid\r\n	LEFT JOIN\r\n		(SELECT uid,COUNT(*) total FROM {pre}sellers WHERE type=\'3\' GROUP BY uid) t3\r\n	ON \r\n		t3.uid=t0.uid) t1\r\nON\r\n	t1.uid=t0.uid\r\nSET\r\n	t0.sellers1=t1.sellers1,t0.sellers2=t1.sellers2,t0.sellers3=t1.sellers3;\r\nUPDATE \r\n	{pre}memberfields t0 \r\nLEFT JOIN \r\n	(SELECT \r\n		t0.uid,IFNULL(t1.total,0) buyers1,IFNULL(t2.total,0) buyers2,IFNULL(t3.total,0) buyers3\r\n	FROM\r\n		{pre}memberfields t0\r\n	LEFT JOIN\r\n		(SELECT uid,COUNT(*) total FROM {pre}buyers WHERE type=\'1\' GROUP BY uid) t1\r\n	ON\r\n		t1.uid=t0.uid\r\n	LEFT JOIN\r\n		(SELECT uid,COUNT(*) total FROM {pre}buyers WHERE type=\'2\' GROUP BY uid) t2\r\n	ON\r\n		t2.uid=t0.uid\r\n	LEFT JOIN\r\n		(SELECT uid,COUNT(*) total FROM {pre}buyers WHERE type=\'3\' GROUP BY uid) t3\r\n	ON \r\n		t3.uid=t0.uid) t1\r\nON\r\n	t1.uid=t0.uid\r\nSET\r\n	t0.buyers1=t1.buyers1,t0.buyers2=t1.buyers2,t0.buyers3=t1.buyers3','');
